          <br />
          <button type="submit">Create Ticket</button>
        </form>
        <h2>Ticket Count: {ticketCount}</h2>
        <h2>Resolved Tickets</h2>
        <ul>
          {resolvedTickets.map((ticket, index) => (
            <li key={index}>
              <p>{ticket.title}</p>
              <p>{ticket.issue}</p>
              <p>{ticket.description}</p>
              <p>{ticket.difficulty}</p>
